//STEP 1: run this code to setup, or to start again

//how many inputs from the LC window?
var numInputs = 1;
//how many parameters are you controlling?
var numOutputs = 2;
//the sizes of the middle layers - increase the size/complexity for more complex mappings
var structure = [30,30];
//CPU or GPU?  set this to suit your machine / task
var useCPU = 1;
//send info to the console?
var debug = 1;


tf.setBackend(useCPU ? 'cpu' : 'webgl');

var currentInputs = new Array(numInputs);
var state = new Array(numOutputs);

//modes for machine learning
var MLMODES = {NONE:0,TRAIN:1,PREDICT:2};
var mode = MLMODES.NONE;

//setup channels to send data to LC
var outChannels = [];
for(let i=0; i < numOutputs; i++) {
	outChannels[i]= createOutputChannel(i, 1);
}

//a function to set data to the LC window
function sendState(st) {
	for(i in outChannels) {
		outChannels[i].send(st[i]);
	}
}

//record inputs
var sources = [];
//record states as targets for the model
var targets = [];

var inputMap = {
	0:(x)=>{
		//record the inputs
		for(let i=0; i < x.length; i++) {
			currentInputs[i] = x[i];
		}
		//record the mouse values
		if (mode == MLMODES.TRAIN) {
			//collect training data
			sources.push(Array.from(currentInputs));	
			targets.push(state);

			console.log(sources.length, "samples collected");
		}else if (mode == MLMODES.PREDICT) {
			//make a prediction from the model
			let modelInput = tf.tensor2d(currentInputs,[1,currentInputs.length]);
			let prediction = model.predict(modelInput).dataSync();
			sendState(prediction);
			if (debug) {
				console.log(currentInputs, prediction)
			}
		}
	},
};

//receive data from the LC window
input = (id,x) => {
	if (inputMap[id]) {
		inputMap[id](x);
	}
};

//this function generates a random list of numbers.We'll use it to explore the parameter space
function randomList(n) {
	let x = [];
	for(let i=0; i < n; i++) x.push(Math.random());
	return x;
}

_____



//:::2::: EXPLORATION OF THE SYNTH

//run this code block to randomise the parameters - repeat until you find a sound that you'd like to train as a target for the network
//or specify the state manually: state = [x1,x2...]
state = randomList(numOutputs);
sendState(state);
state
___

//:::3::: TRAINING DATA COLLECTION
//3a: prepare your input for recording
//:::3b::: run this block to collect data which will be mapped to your current state
mode = MLMODES.TRAIN;
___
//:::3c::: run this block to stop collecting data
mode = MLMODES.NONE;
//:::3d:::repeat from step 2
___


_____
//:::4::: MODEL CREATION

//create a neural network model.
var model = tf.sequential();

structure.forEach((layerSize,i) => {
	model.add(tf.layers.dense({
		inputShape:i==0? [numInputs] : undefined,
		units: layerSize,
		activation: 'relu',
		kernelInitialiser: 'leCunNormal' }));	
});

model.add(tf.layers.dense({ units: outChannels.length }));
model.compile({ loss: 'meanSquaredError', optimizer: tf.train.adam() });

model.summary();
____

//:::5::: DATA CONVERSION

//Convert the data we've collected into the right format for tensorflow
var trainIn = tf.tensor2d(sources, [sources.length, numInputs]);
var trainOut = tf.tensor2d(targets, [targets.length, numOutputs]);

____

//:::6::: TRAINING
//train the model.  You can repeat this step as desired until the loss is low enough

function onBatchEnd (x, logs){console.log(x, logs)};

model.fit(trainIn, trainOut, { epochs: 50, callbacks: {onBatchEnd} }).then(info => {console.log(info);});

_____

//:::7::: PREDICTION
//now send the model some input
mode = MLMODES.PREDICT;

___
//turn off prediction -> add training data? start again? 
mode = MLMODES.NONE;

____
